import React,{useEffect,useState  }from "react";
import {
    SafeAreaView,
    View,
    Text,
    TextInput,
    TouchableOpacity,
    TouchableHighlight,
    Image,
    StyleSheet,
    ScrollView,
    ImageBackground,
    
  } from "react-native";
  import {Card,Title,Paragraph,BottomNavigation} from "react-native-paper"; 
  
  import Icon3 from "react-native-vector-icons/AntDesign";
  import Icon from "react-native-vector-icons/MaterialIcons";
  import Icon1 from "react-native-vector-icons/EvilIcons";
  import Icon2 from "react-native-vector-icons/AntDesign";
 
 




const image = { uri: "https://docs.expo.dev/static/images/tutorial/splash.png" };
  const ProduceScreen = ({ route,navigation }) => {
    let Cart=[]
    const [isLoading, setLoading] = useState(true);
    const [ categoryArray,setcategoryArray] =useState([])
    const [ productArray,setproductArray] =useState([])
    const [showCounterTip, setCounterTip] = useState(false);
  const [showAddTip, setAddTip] = useState(true);
  const [Counter, setCounter] = useState(0);
  const [showBillTip, setBillTip] = useState(false);
    const farm_id = route.params
    console.log(farm_id)
    let API ="http://192.168.0.108:3000/api/v1/category/"+farm_id.farm_id
    console.log(API)
  
  
    useEffect(() => {
      console.log("hi");
      fetch(API, {
        method: "GET",
      })
        .then((response) => response.json())
        .then((jsonData) => {
          console.log(jsonData);
          setcategoryArray(jsonData)
          console.log(categoryArray);
          setLoading(false);
        }).catch((error)=>{
          console.log("error",error)
        });
    },[]);
    const GetCategoryProducts=(category_id)=>{
      console.log("hello",category_id)
      let Category_API="http://192.168.0.108:3000/api/v1/product/"+category_id
      fetch(Category_API,{
        method:"GET"
      }) .then((response) => response.json())
      .then((jsonData) => {
        console.log(jsonData)
        setproductArray(jsonData)
        setLoading(false)

      }).catch((error)=>{
        console.log("error",error)
      });
      

    }
 
  
    return(
      <SafeAreaView
      style={{ flex: 1, backgroundColor:"wheat",paddingTop:40}}>
        
    
        <View style={{width:327,height:92,marginLeft:16}}>
        <TouchableOpacity onPress={() => navigation.navigate("ProductScreen")}>
        <View style={{display:'flex',flexDirection:'row'}}>
            <Icon3
              name="arrowleft"
              color="#006C48"
              size={10}
              style={{ paddingRight: 20, paddingTop: 4 }}
            />
            <Text>Back to Home</Text>
            </View>
            </TouchableOpacity>
            <View >
                <Text style={{fontWeight:'700',fontSize:28,paddingTop:10}}>Produce</Text>
            </View>
        </View>
           
            <View style={{display:'flex',flexDirection:'row',marginTop:-20}}>
           
            
          
                <View style={{width:98,height:604,marginLeft:24,backgroundColor:"wheat",justifyContent:'space-between',
                alignItems:'center',display:'flex',flexDirection:'column'}}>
                  {
                    categoryArray !=null && (
                      categoryArray.map((a)=>{
                        return (
                          <>
                          <TouchableOpacity onPress={()=>{
                            GetCategoryProducts(a._id)

                          }}>
                            <View  style={{width:97,height:114,backgroundColor:"white",justifyContent:'center',alignItems:'center',
                    borderRadius:24}}> 
                   
                    <ImageBackground source={{
uri:
'https://images.pexels.com/photos/1978126/pexels-photo-1978126.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=650&w=940',
}}
style={{width:74,height:74,borderRadius:12}}></ImageBackground>
                 <Text>{a.name}</Text>

                    </View> 
                    </TouchableOpacity>
                          </>
                        )
                      })
                    )
                  }
                  
                  


                </View> 


          
          
                
<View style={{display:'flex',justifyContent:'space-between',marginLeft:40}}>
  {
    productArray!=null && (
      productArray.map((a)=>{
        return (
          <>
         
          <View>
  <Card style={{width:207,height:189,backgroundColor:"#F6FEFF"}}>
    {/* <Card.Cover source={{ uri: 'https://picsum.photos/700' }} style={{width:207,height:130}}
    /> */}
    <ImageBackground source={{uri: 'https://picsum.photos/700' }} style={{width:207,height:130}}> 
<View style={{width:43,height:24,backgroundColor:"#BEFFDA",position:'absolute',marginTop:100,marginLeft:15,alignItems:"center"}}>
<Text style={{fontWeight:'500',fontSize:14}}>{a.product_price}<Text>â‚¹</Text></Text>

</View>

    </ImageBackground>
    <Card.Content>
          
            <View style={{display:'flex',flexDirection:'row',alignContent:'center',alignItems:'center',justifyContent:'space-between'}}>
            <View>
           <Title>{a.product_name}</Title>
           <Paragraph style={{fontFamily:"Roboto",fontSize:12}}>
                Lorem Farms</Paragraph>
           </View>
           {/* <View style={{width:66,height:25,backgroundColor:"white",borderRadius:4,
           borderColor:"#006C48",alignItems:'center',justifyContent:"center",marginTop:25}}> */}
            <View>
{
          a.removeAdd && (
           
                <View>
                   <TouchableOpacity onPress={()=>{
                    a.isAdded=true
                    a.removeAdd=false
                    a.product_Count=a.product_Count+1
                    Cart.push(a)
                    console.log(Cart)
                    
                    
              
              
              setBillTip(true)
              
            }}>
            <Text style={{color:'green'}}>ADD</Text>
            </TouchableOpacity>
        </View>
          
        

          )

        }
        {
          a.isAdded && (
           
            <View>
            
            <View style={{display:"flex",flexDirection:"row",alignItems:"center"
          }}>
            <TouchableOpacity onPress={()=>{
              if(a.product_Count>0){
                
                a.product_Count=a.product_Count-1

              }
              else{
                // setCounterTip(false)
                // setAddTip(true)
                

              }
              
            }}>
            <Text style={{color:'green',paddingLeft:10,fontWeight:"bold"}}>-</Text>
            </TouchableOpacity>
            <Text style={{color:'green' ,paddingLeft:10}}>{a.product_Count}</Text>
            <TouchableOpacity onPress={()=>{
              setCounter(Counter+1)
            }}>
            <Text style={{color:'green',paddingLeft:10}}>+</Text>
            </TouchableOpacity>


              </View>
            
            
        </View>
        
            
          )}

</View>

           </View>
            {/* </View> */}
          
          
           
          </Card.Content>

    </Card>
  </View>
  
  <View style={{padding:5}}></View>

          </>
        )
      })
    )
  }
  
  
</View>




            </View>
            <View>
            {
        showBillTip && (
          <View style={{backgroundColor:"white",width:340,height:70,marginLeft:10,marginBottom:62,borderRadius:12}}>
          <View style={{display:"flex",flexDirection:"row",padding:10,alignItems:"center"}}>
          
          <View style={{marginLeft:10}}>
            <Text>Farm LoremIpsum</Text>
            <Text>Total: 190.85</Text>
          </View>
          <TouchableOpacity onPress={() => navigation.navigate("CheckoutScreen")}>
          <View style={{marginLeft:100,width:110,height:40,borderRadius:100,backgroundColor:"#006C48",justifyContent:"center",alignItems:"center"}}>
            <View>
              
              <Text style={{color:"white"}}>
                View Cart
              </Text>
                
              
            </View>
            
          </View>
          </TouchableOpacity>

          </View>
         
        </View>
        )
       }

            </View>
            <View style={{backgroundColor:"#F6FEFF",width:412,height:70 ,position:"absolute",bottom:0  }}>
    <View style={{display:'flex',flexDirection:'row',alignItems:'center',flex:1}}>
  <View style={{paddingLeft:36}}>
    <TouchableOpacity>
    <Icon2
              name="tago"
              color="#006C48"
              size={30}
              
            />
    </TouchableOpacity>
  
  <Text>Home</Text>          
  </View>
  <View style={{paddingLeft:95}}>
  <Icon2
              name="tago"
              color="#006C48"
              size={30}
              
            />
  <Text>Community</Text>          
  </View>
  <View style={{paddingLeft:95}}>
    <TouchableOpacity>
    <Icon
              name="person-outline"
              color="#006C48"
              size={30}
              style={{ paddingRight: 20, paddingTop: 4 }}
            />
    </TouchableOpacity>
 
  <Text>Profile</Text>          
  </View>


    </View>
    </View>

        
        
        
        </SafeAreaView>
     
    
      
     
     

      
        );
    };
   
  
   
    
  export default ProduceScreen;
